import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [driftData, setDriftData] = useState([]);

  useEffect(() => {
    // Fetch drift data from the backend
    axios.get('http://localhost:5007/drift')
      .then(response => {
        setDriftData(response.data);
      })
      .catch(error => {
        console.error('Error fetching drift data:', error);
      });
  }, []);

  return (
    <div className="App">
      <h1>LineAlert Dashboard</h1>

      {/* Drift Data Section */}
      <div className="drift-data-section">
        <h2>Drift Data</h2>
        {driftData.length > 0 ? (
          driftData.map((data, index) => (
            <div key={index} className="drift-card">
              <p>Tag: {data.tag}</p>
              <p>Value: {data.value}</p>
              <p>Severity: {data.severity}</p>
              <p>Change: {data.change}</p>
              <p>Timestamp: {data.timestamp || "No Timestamp Available"}</p>
            </div>
          ))
        ) : (
          <p>No Drift Data Available</p>
        )}
      </div>
    </div>
  );
}

export default App;
