import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
    const [alerts, setAlerts] = useState([]);
    const [driftData, setDriftData] = useState([]);
    const [filter, setFilter] = useState('All Alerts');

    useEffect(() => {
        fetchAlerts();
        fetchDriftData();
    }, []);

    const fetchAlerts = async () => {
        try {
            const response = await axios.get('http://localhost:5007/alerts');
            setAlerts(response.data);
        } catch (error) {
            console.error("Error fetching alerts: ", error);
        }
    };

    const fetchDriftData = async () => {
        try {
            const response = await axios.get('http://localhost:5007/drift');
            setDriftData(response.data);
        } catch (error) {
            console.error("Error fetching drift data: ", error);
        }
    };

    const handleFilterChange = (e) => {
        setFilter(e.target.value);
    };

    const filteredAlerts = alerts.filter(alert => {
        if (filter === 'All Alerts') return true;
        return alert.severity === filter;
    });

    return (
        <div className="App">
            <h1>LineAlert Dashboard</h1>
            <div className="filter">
                <button value="All Alerts" onClick={handleFilterChange}>All Alerts</button>
                <button value="Critical Alerts" onClick={handleFilterChange}>Critical Alerts</button>
                <button value="Low Alerts" onClick={handleFilterChange}>Low Alerts</button>
            </div>

            <div className="alerts">
                <h2>Alerts</h2>
                {filteredAlerts.map((alert, index) => (
                    <div key={index} className={`alert ${alert.severity.toLowerCase()}`}>
                        <p>Change: {alert.change}</p>
                        <p>Pressure: {alert.pressure}</p>
                        <p>Severity: {alert.severity}</p>
                        <p>Timestamp: {alert.timestamp}</p>
                    </div>
                ))}
            </div>

            <div className="drift-data">
                <h2>Drift Data</h2>
                {driftData.map((data, index) => (
                    <div key={index} className="drift-item">
                        <p>Tag: {data.tag}</p>
                        <p>Value: {data.value}</p>
                        <p>Severity: {data.severity}</p>
                        <p>Change: {data.change}</p>
                        <p>Timestamp: {data.timestamp || "No Timestamp Available"}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default App;
