import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [alerts, setAlerts] = useState([]);
  const [driftData, setDriftData] = useState([]);
  const [alertFilter, setAlertFilter] = useState('All Alerts');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const alertsResponse = await axios.get('http://localhost:5007/alerts');
        const driftResponse = await axios.get('http://localhost:5007/drift');
        setAlerts(alertsResponse.data);
        setDriftData(driftResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // Fetch data every 5 seconds

    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, []);

  const filterAlerts = (severity) => {
    if (severity === 'All Alerts') {
      return alerts;
    }
    return alerts.filter(alert => alert.severity === severity);
  };

  return (
    <div>
      <h1>LineAlert Dashboard</h1>

      <div>
        <button onClick={() => setAlertFilter('All Alerts')}>All Alerts</button>
        <button onClick={() => setAlertFilter('Critical Alerts')}>Critical Alerts</button>
        <button onClick={() => setAlertFilter('Low Alerts')}>Low Alerts</button>
      </div>

      <div>
        <h2>Alerts</h2>
        {filterAlerts(alertFilter).map((alert, index) => (
          <div key={index} className={`alert ${alert.severity.toLowerCase()}`}>
            <p>Change: {alert.change}</p>
            <p>Pressure: {alert.pressure}</p>
            <p>Severity: {alert.severity}</p>
            <p>Timestamp: {alert.timestamp}</p>
          </div>
        ))}
      </div>

      <div>
        <h2>Drift Data</h2>
        {driftData.map((data, index) => (
          <div key={index} className={`drift-data ${data.severity.toLowerCase()}`}>
            <p>Tag: {data.tag}</p>
            <p>Value: {data.value}</p>
            <p>Severity: {data.severity}</p>
            <p>Change: {data.change}</p>
            <p>Timestamp: {data.timestamp}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
