import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [alerts, setAlerts] = useState([]);
  const [driftData, setDriftData] = useState([]);
  const [selectedFilter, setSelectedFilter] = useState("All");

  useEffect(() => {
    // Fetch alerts data
    axios.get('http://localhost:5007/alerts')
      .then(response => {
        setAlerts(response.data);
      })
      .catch(error => console.error('Error fetching alerts:', error));

    // Fetch drift data
    axios.get('http://localhost:5007/drift')
      .then(response => {
        setDriftData(response.data);
      })
      .catch(error => console.error('Error fetching drift data:', error));
  }, []);

  // Function to filter alerts based on severity
  const filterAlerts = (severity) => {
    return alerts.filter(alert => severity === "All" || alert.severity === severity);
  };

  return (
    <div className="App">
      <h1>LineAlert Dashboard</h1>
      <div className="filter-buttons">
        <button onClick={() => setSelectedFilter("All")}>All Alerts</button>
        <button onClick={() => setSelectedFilter("Critical")}>Critical Alerts</button>
        <button onClick={() => setSelectedFilter("Low")}>Low Alerts</button>
      </div>

      <div className="alerts-section">
        <h2>Alerts</h2>
        {filterAlerts(selectedFilter).map((alert, index) => (
          <div key={index} className={`alert-card ${alert.severity}`}>
            <p>Change: {alert.change}</p>
            <p>Pressure: {alert.pressure}</p>
            <p>Severity: {alert.severity}</p>
            <p>Timestamp: {alert.timestamp}</p>
          </div>
        ))}
      </div>

      <div className="drift-data-section">
        <h2>Drift Data</h2>
        {driftData.map((data, index) => (
          <div key={index} className="drift-card">
            <p>Tag: {data.tag}</p>
            <p>Value: {data.value}</p>
            <p>Severity: {data.severity}</p>
            <p>Change: {data.change}</p>
            <p>Timestamp: {data.timestamp || "No Timestamp Available"}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
