import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [driftData, setDriftData] = useState([]);
  const [alertData, setAlertData] = useState([]);
  const [filter, setFilter] = useState("All");

  // Fetch drift data
  const fetchData = async () => {
    try {
      const driftResponse = await axios.get("http://localhost:5007/drift");
      const alertsResponse = await axios.get("http://localhost:5007/alerts");
      setDriftData(driftResponse.data);
      setAlertData(alertsResponse.data);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  // Set up interval to fetch data periodically
  useEffect(() => {
    fetchData();
    const interval = setInterval(() => {
      fetchData();
    }, 5000); // Refresh every 5 seconds

    return () => clearInterval(interval);
  }, []);

  // Filter data based on alert severity
  const filteredAlerts = alertData.filter((alert) => {
    if (filter === "All") return true;
    return alert.severity === filter;
  });

  return (
    <div className="App">
      <h1>LineAlert Dashboard</h1>

      {/* Filter Options */}
      <div>
        <button onClick={() => setFilter("All")}>All Alerts</button>
        <button onClick={() => setFilter("Critical")}>Critical Alerts</button>
        <button onClick={() => setFilter("Low")}>Low Alerts</button>
      </div>

      {/* Drift Data Display */}
      <div>
        <h2>Drift Data</h2>
        {driftData.length > 0 ? (
          driftData.map((data, index) => (
            <div key={index}>
              <p>Tag: {data.tag}</p>
              <p>Value: {data.value}</p>
              <p>Severity: {data.severity}</p>
              <p>Change: {data.change}</p>
              <p>Timestamp: {data.timestamp || "No Timestamp Available"}</p>
              <hr />
            </div>
          ))
        ) : (
          <p>No Drift Data Available</p>
        )}
      </div>

      {/* Alerts Section */}
      <div>
        <h2>Alerts</h2>
        {filteredAlerts.length > 0 ? (
          filteredAlerts.map((alert, index) => (
            <div key={index}>
              <p>Change: {alert.change}</p>
              <p>Pressure: {alert.pressure}</p>
              <p>Severity: {alert.severity}</p>
              <p>Timestamp: {alert.timestamp}</p>
              <hr />
            </div>
          ))
        ) : (
          <p>No Alerts Available</p>
        )}
      </div>
    </div>
  );
}

export default App;
