import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [driftData, setDriftData] = useState([]);
  const [alertData, setAlertData] = useState([]);
  const [filter, setFilter] = useState('All');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const driftResponse = await axios.get('http://localhost:5007/drift');
        setDriftData(driftResponse.data);

        const alertsResponse = await axios.get('http://localhost:5007/alerts');
        setAlertData(alertsResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('There was an error fetching data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const filteredDriftData = driftData.filter((data) => {
    if (filter === 'All') return true;
    return data.severity === filter;
  });

  const filteredAlertData = alertData.filter((data) => {
    if (filter === 'All') return true;
    return data.severity === filter;
  });

  return (
    <div>
      <h1>LineAlert Dashboard</h1>

      {/* Filter Buttons */}
      <div>
        <button onClick={() => setFilter('All')}>All Alerts</button>
        <button onClick={() => setFilter('Bad')}>Critical Alerts</button>
        <button onClick={() => setFilter('Low')}>Low Alerts</button>
      </div>

      {/* Error Message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Loading Spinner */}
      {loading && <p>Loading data...</p>}

      {/* Drift Data */}
      <h2>Drift Data</h2>
      <div>
        {filteredDriftData.map((data, index) => (
          <div key={index}>
            <p>Tag: {data.tag}</p>
            <p>Value: {data.value}</p>
            <p>Severity: {data.severity}</p>
            <p>Change: {data.change}</p>
            <p>Timestamp: {data.timestamp || 'No Timestamp Available'}</p>
          </div>
        ))}
      </div>

      {/* Alert Data */}
      <h2>Alerts</h2>
      <div>
        {filteredAlertData.map((alert, index) => (
          <div key={index}>
            <p>Change: {alert.change}</p>
            <p>Pressure: {alert.pressure}</p>
            <p>Severity: {alert.severity}</p>
            <p>Timestamp: {alert.timestamp}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
