import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [driftData, setDriftData] = useState([]);

  // Fetch data function
  const fetchData = async () => {
    try {
      const driftResponse = await axios.get("http://localhost:5007/drift");
      setDriftData(driftResponse.data);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  // Fetch data every 5 seconds
  useEffect(() => {
    fetchData();
    const interval = setInterval(() => {
      fetchData();
    }, 5000); // Refresh every 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <h1>LineAlert Dashboard</h1>

      {/* Drift Data Display */}
      <div>
        <h2>Drift Data</h2>
        {driftData.length > 0 ? (
          driftData.map((data, index) => (
            <div key={index}>
              <p>Tag: {data.tag}</p>
              <p>Value: {data.value}</p>
              <p>Severity: {data.severity}</p>
              <p>Change: {data.change}</p>
              <p>Timestamp: {data.timestamp || "No Timestamp Available"}</p>
              <hr />
            </div>
          ))
        ) : (
          <p>No Drift Data Available</p>
        )}
      </div>
    </div>
  );
}

export default App;
